import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import InputGroup from '../UI/TextFieldGroup/InputGroup';
import SelectListGroup from '../UI/TextFieldGroup/SelectListGroup';
import TextAreaGroup from '../UI/TextFieldGroup/TextAreaGroup';
import TextFieldGroup from '../UI/TextFieldGroup/TextFieldGroup';

class CreateProfile extends Component {
    state = {
        displaySocialInputs: false,
        handle: '',
        company: '',
        website: '',
        location: '',
        status: '',
        skills: '',
        githubusername: '',
        bio: '',
        twitter: '',
        facebook: '',
        linkedin: '',
        youtube: '',
        instagram: '',
        errors: {}
    };

    changeInputHandler = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    submitFormHandler = (e) => {
        e.preventDefault();

        console.log('submit');
    }

    render() {

        const { errors, displaySocialInputs } = this.state

        let socialInputs;

        if(displaySocialInputs) {
            socialInputs = (
                <div>
                    <InputGroup
                    placeholder="Twitter Profile URL"
                    name="twitter"
                    icon="fab fa-twitter"
                    value={this.state.twitter}
                    onChange={this.onChange}
                    error={errors.twitter}
                    />

                    <InputGroup
                    placeholder="Facebook Page URL"
                    name="facebook"
                    icon="fab fa-facebook"
                    value={this.state.facebook}
                    onChange={this.onChange}
                    error={errors.facebook}
                    />

                    <InputGroup
                    placeholder="Linkedin Profile URL"
                    name="linkedin"
                    icon="fab fa-linkedin"
                    value={this.state.linkedin}
                    onChange={this.onChange}
                    error={errors.linkedin}
                    />

                    <InputGroup
                    placeholder="YouTube Channel URL"
                    name="youtube"
                    icon="fab fa-youtube"
                    value={this.state.youtube}
                    onChange={this.onChange}
                    error={errors.youtube}
                    />

                    <InputGroup
                    placeholder="Instagram Page URL"
                    name="instagram"
                    icon="fab fa-instagram"
                    value={this.state.instagram}
                    onChange={this.onChange}
                    error={errors.instagram}
                    />
                </div>
            )
        }

        const options = [
            { label: '* Select Professional Status', value: 0 },
            { label: 'Junior Front-End', value: 'Junior Front-End' },
            { label: 'Senior Front-End', value: 'Senior Front-End' },
            { label: 'Junior Back-End', value: 'Junior Back-End' },
            { label: 'Senior Back-End', value: 'Senior Back-End' },
            { label: 'Full Stack', value: 'Full Stack' },
            { label: 'UI Designer', value: 'UI Designer' },
            { label: 'Product Owner', value: 'Product Owner' },
            { label: 'Intern', value: 'Intern' },
            { label: 'Teacher', value: 'Teacher' },
            { label: 'Student', value: 'Student' },
            { label: 'Other', value: 'Other' },

        ];

        return (
            <div className="create-profile">
                <div className="container">
                    <div className="row">
                        <div className="col-md-8 m-auto">
                            <h1 className="display-4 text-center">Create your profile</h1>
                            <p className="lead text-center">
                                Lets get some information to make your profile EXQUISITE
                            </p>
                            <small className="d-block pb-3">* = required fields</small>
                            <form onSubmit={this.submitFormHandler}>
                                <TextFieldGroup
                                    placeholder="* Profile Handle"
                                    name="handle"
                                    value={this.state.handle}
                                    change={this.changeInputHandler}
                                    error={errors.handle}
                                    info="A unique handle for your profile URL. Your full name, company name, nickname" />
                                <SelectListGroup
                                    placeholder="Status"
                                    name="status"
                                    value={this.state.status}
                                    change={this.changeInputHandler}
                                    options={options}
                                    error={errors.status}
                                    info="What label you want to put on yourself?" />
                                <TextFieldGroup
                                    placeholder="Company"
                                    name="company"
                                    value={this.state.company}
                                    change={this.changeInputHandler}
                                    error={errors.company}
                                    info="Could be your own company or one you slave for" />
                                <TextFieldGroup
                                    placeholder="Website"
                                    name="website"
                                    value={this.state.website}
                                    onChange={this.changeInputHandler}
                                    error={errors.website}
                                    info="Could be your own website or a company one" />
                                <TextFieldGroup
                                    placeholder="Location"
                                    name="location"
                                    value={this.state.location}
                                    onChange={this.changeInputHandler}
                                    error={errors.location}
                                    info="City or city & state suggested (eg. Tallinn, Harjumaa)" />
                                <TextFieldGroup
                                    placeholder="* Skills"
                                    name="skills"
                                    value={this.state.skills}
                                    onChange={this.changeInputHandler}
                                    error={errors.skills}
                                    info="Please use comma separated values (eg.HTML,CSS,JavaScript,PHP" />
                                <TextFieldGroup
                                    placeholder="Github Username"
                                    name="githubusername"
                                    value={this.state.githubusername}
                                    onChange={this.changeInputHandler}
                                    error={errors.githubusername}
                                    info="If you want your latest repos and a Github link, include your username" />
                                <TextAreaGroup
                                    placeholder="Short Bio"
                                    name="bio"
                                    value={this.state.bio}
                                    onChange={this.changeInputHandler}
                                    error={errors.bio}
                                    info="I REALLY want to know more about you..." />

                                <div className="mb-3">
                                    <button onClick={() => {
                                        this.setState(prevState => ({
                                            displaySocialInputs: !prevState.displaySocialInputs
                                        }))
                                    }} className="btn btn-light">
                                        Add Social Network Links
                                    </button>
                                    <span className="text-muted">Optional</span>
                                </div>
                                {socialInputs}
                                <input type="submit" value="submit" className="btn btn-info btn-block mt-4" />
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

CreateProfile.propTypes = {
    profile: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    profile: state.profile,
    errors: state.errors
});

export default connect(mapStateToProps)(CreateProfile);
