import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import { Link } from 'react-router-dom';
import { deletePost, addLike, removeLike } from '../../../actions/postActions';

class PostItem extends Component {

    deletePostHandler = (e, id) => {
        this.props.deletePost(id);
    }

    addLikeHandler = (e, id) => {
        this.props.addLike(id);
    }

    dislikeHandler = (e, id) => {
        this.props.removeLike(id);
    }

    findUserLike(likes) {
        const { auth } = this.props;

        if(likes.filter(like => like.user === auth.user.id).length > 0) {
            return true;
        } else {
            return false;
        }
    }

    render() {

        const { post, auth } = this.props;

        return (
            <div className="card card-body mb-3">
                <div className="row">
                    <div className="col-md-2">
                        <p className="text-center">{post.name}</p>
                    </div>
                    <div className="col-md-10">
                        <p className="lead">{post.text}</p>
                        {showActions ? (<span>
                            <button
                                onClick={((e) => this.addLikeHandler(e, post._id))}
                                type="button" 
                                className="btn btn-light mr-1">
                                <i className={classnames('fa fa-thumbs-up', {
                                    'text-info' : this.findUserLike(post.likes)
                                })}></i>
                                    <span className="badge badge-light">
                                        {post.likes.length}
                                    </span>
                            </button>
                            <button
                                onClick={((e) => this.dislikeHandler(e, post._id))} 
                                type="button" 
                                className="btn btn-light mr-1">
                                <i className="text-secondary fa fa-thumbs-down"></i>
                            </button>
                            <Link to={`/post/${post._id}`} className="btn btn-info mr-1">
                                Comments
                            </Link>
                            {post.user === auth.user.id ? (
                            <button 
                                onClick={((e) => this.deletePostHandler(e, post._id))} 
                                type="button" 
                                className="btn btn-danger mr-1">
                                <i className="">Delete</i>
                            </button>
                            ) : null}
                        </span>) : null} 
                    </div>
                </div>
            </div>
        )
    }
};

PostItem.defaultProps = {
    showActions: true
}

PostItem.propTypes = {
    addLike: PropTypes.func.isRequired,
    removeLike: PropTypes.func.isRequired,
    deletePost: PropTypes.func.isRequired,
    post: PropTypes.object.isRequired,
    auth: PropTypes.object.isRequired
};

const mapStateToProps = (state) => ({
    auth: state.auth
});

export default connect(mapStateToProps, { deletePost, addLike, removeLike })(PostItem);
